'use client'

import { useState, useEffect } from 'react'
import { Pencil, Menu, ChevronLeft, ChevronRight } from 'lucide-react'
import Link from 'next/link'
import { useRouter } from 'next/router'; 

const nfts = [
  { id: 1, name: 'Cool Cat #1', price: '0.1 ETH', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUIWPXHFYOoqszi_WONByguDtKthPvU8q3Ew&s' },
  { id: 2, name: 'Bored Ape #42', price: '0.2 ETH', image: 'https://static.vecteezy.com/system/resources/thumbnails/019/159/086/small/helmet-for-nft-which-can-be-used-as-accessories-traits-or-assets-which-could-be-placed-on-any-head-character-and-used-it-as-traits-for-your-nft-vector.jpg' },
  { id: 3, name: 'Crypto Punk #007', price: '0.15 ETH', image: 'data:image/jpeg;base64,' },
  { id: 4, name: 'Doodle #123', price: '0.08 ETH', image: 'https://static.vecteezy.com/system/resources/thumbnails/019/469/880/small/hat-cap-accessories-traits-which-can-be-used-as-accessories-traits-assets-which-could-be-placed-on-any-head-character-and-use-it-as-traits-for-your-nft-vector.jpg' },
  { id: 5, name: 'Azuki #56', price: '0.12 ETH', image: 'data:image/jpeg;base64,' },
  { id: 6, name: 'CloneX #789', price: '0.18 ETH', image: 'data:image/jpeg;base64,' },
  { id: 7, name: 'Moonbird #101', price: '0.25 ETH', image: 'data:image/jpeg;base64,' },
  { id: 8, name: 'Pudgy Penguin #22', price: '0.09 ETH', image: 'data:image/jpeg;base64,' },
]

const ITEMS_PER_PAGE = 6

export default function Dashboard({onClick}: any) {
  const [isWalletConnected, setIsWalletConnected] = useState(true)
  const [userProfile, setUserProfile] = useState({
    name: 'John Doe',
    description: 'NFT enthusiast and collector',
    sex: 'male',
    avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUIWPXHFYOoqszi_WONByguDtKthPvU8q3Ew&s'
  })
  const [editField, setEditField] = useState(null)
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)
  const [currentPage, setCurrentPage] = useState(1)

//   const toggleWalletConnection = () => {
//     setIsWalletConnected(!isWalletConnected)
//   }
const router = useRouter();
  const toggleWalletConnection = () => {
    if (isWalletConnected) {
      setIsWalletConnected(false);
      // Redirect to login page when disconnecting the wallet
      router.push('/auth/login');
    }
  };

  const updateProfile = (field, value) => {
    setUserProfile({ ...userProfile, [field]: value })
    setEditField(null)
  }

  const totalPages = Math.ceil(nfts.length / ITEMS_PER_PAGE)
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE
  const endIndex = startIndex + ITEMS_PER_PAGE
  const currentNfts = nfts.slice(startIndex, endIndex)

  // Close the mobile menu if the user clicks outside of it
  useEffect(() => {
    const handleClickOutside = (event) => {
      const menu = document.getElementById('mobile-menu');
      if (menu && !menu.contains(event.target) && !event.target.closest('button')) {
        setIsMobileMenuOpen(false)
      }
    }
    document.addEventListener('click', handleClickOutside)
    return () => document.removeEventListener('click', handleClickOutside)
  }, [])

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-300 via-yellow-200 to-white p-4 sm:p-6 md:p-8">
      <div className="max-w-6xl mx-auto">
        {/* <header className="flex justify-between items-center mb-6 md:mb-8">
          <h1 className="text-2xl sm:text-3xl font-bold text-black">NFT Dashboard</h1>
          <div className="flex items-center space-x-2 sm:space-x-4">
            <div className="md:hidden">
              <button 
                className="p-2 bg-transparent border-2 rounded-full" 
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              >
                <Menu className="h-6 w-6" />
              </button>
            </div>
            <div
              id="mobile-menu"
              className={`${isMobileMenuOpen ? 'flex' : 'hidden'} md:flex flex-col md:flex-row items-center space-y-2 md:space-y-0 md:space-x-4 absolute md:relative top-16 right-4 md:top-0 md:right-0 bg-white md:bg-transparent p-4 md:p-0 rounded-lg shadow-lg md:shadow-none transition-transform duration-300 transform ${isMobileMenuOpen ? 'translate-x-0' : 'translate-x-full'}`}
            >
                <button onClick={() => alert('Starting chat...')} className="w-full md:w-auto bg-yellow-400 hover:bg-yellow-500 text-black py-2 px-4 rounded-lg">
                Start Chat
              </button>
              <button 
                onClick={toggleWalletConnection} 
                className={`w-full md:w-auto py-2 px-4 rounded-lg ${isWalletConnected ? 'bg-green-500 hover:bg-green-600' : 'bg-yellow-400 hover:bg-yellow-500'} text-black transition-colors duration-300`}
              >
                {isWalletConnected ? 'Disconnect' : 'Connect Wallet'}
              </button>
              <button className="w-12 h-12 rounded-full p-0 overflow-hidden">
                <img src={userProfile.avatar} alt="Profile" className="w-full h-full object-cover" />
              </button>
            </div>
          </div>
        </header> */}
             <header className="flex justify-between items-center mb-6 md:mb-8">
          <h1 className="text-2xl sm:text-3xl font-bold text-black">NFT Dashboard</h1>
          <div className="flex items-center space-x-2 sm:space-x-4">
            <div className="md:hidden">
              {/* <button
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                className="w-10 h-10 bg-white rounded-full flex justify-center items-center shadow-md"
              >
                <Menu className="h-6 w-6" />
              </button> */}
                <button 
                className="p-2 bg-yellow-400 border-2 rounded-full" 
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              >
                <Menu className="h-6 w-6" />
              </button>
            </div>
            <div className={`${isMobileMenuOpen ? 'flex' : 'hidden'} md:flex flex-col md:flex-row items-center space-y-2 md:space-y-0 md:space-x-4 absolute md:relative top-16 right-4 md:top-0 md:right-0 bg-white md:bg-transparent p-4 md:p-0 rounded-lg shadow-lg md:shadow-none`}>
              <button onClick={onClick} className="w-full md:w-auto bg-yellow-400 hover:bg-yellow-500 text-black py-2 px-4 rounded-lg">
                Start Chat
              </button>
              <button
                onClick={toggleWalletConnection}
                className={`w-full md:w-auto ${isWalletConnected ? 'bg-green-500 hover:bg-green-600' : 'bg-yellow-400 hover:bg-yellow-500'} text-black py-2 px-4 rounded-lg transition-colors duration-300`}
              >
                {isWalletConnected ? 'Disconnect' : 'Connect Wallet'}
              </button>
              <div className="relative">
                <button className="w-12 h-12 rounded-full p-0 overflow-hidden" onClick={() => setEditField(editField ? null : 'profile')}>
                  <img src={userProfile.avatar} alt="Profile" className="w-full h-full object-cover" />
                </button>
                {editField === 'profile' && (
                  <div className="absolute top-full left-0 mt-2 bg-white text-black shadow-lg p-4 rounded-lg w-64">
                    <div className="space-y-2">
                      <div className="flex justify-between">
                        <span className="font-semibold">Name:</span>
                        {editField === 'name' ? (
                          <input
                            id="name"
                            value={userProfile.name}
                            onChange={(e) => setUserProfile({ ...userProfile, name: e.target.value })}
                            className="w-32 p-2 border border-gray-300 rounded-lg"
                            autoFocus
                          />
                        ) : (
                          <span>{userProfile.name}</span>
                        )}
                        <button
                          onClick={() => setEditField(editField === 'name' ? null : 'name')}
                          className="w-8 h-8 bg-gray-200 rounded-full flex justify-center items-center"
                        >
                          <Pencil className="h-4 w-4" />
                        </button>
                      </div>
                      <div className="flex justify-between">
                        <span className="font-semibold">Description:</span>
                        {editField === 'description' ? (
                          <textarea
                            id="description"
                            value={userProfile.description}
                            onChange={(e) => setUserProfile({ ...userProfile, description: e.target.value })}
                            className="w-32 p-2 border border-gray-300 rounded-lg"
                            autoFocus
                          />
                        ) : (
                          <span>{userProfile.description}</span>
                        )}
                        <button
                          onClick={() => setEditField(editField === 'description' ? null : 'description')}
                          className="w-8 h-8 bg-gray-200 rounded-full flex justify-center items-center"
                        >
                          <Pencil className="h-4 w-4" />
                        </button>
                      </div>
                      <div className="flex justify-between text-black">
                        <span className="font-semibold">Sex:</span>
                        {editField === 'sex' ? (
                          <div className="flex items-center space-x-2">
                            <input
                              type="radio"
                              name="sex"
                              value="male"
                              checked={userProfile.sex === 'male'}
                              onChange={() => setUserProfile({ ...userProfile, sex: 'male' })}
                            />
                            <label>Male</label>
                            <input
                              type="radio"
                              name="sex"
                              value="female"
                              checked={userProfile.sex === 'female'}
                              onChange={() => setUserProfile({ ...userProfile, sex: 'female' })}
                            />
                            <label>Female</label>
                            <input
                              type="radio"
                              name="sex"
                              value="other"
                              checked={userProfile.sex === 'other'}
                              onChange={() => setUserProfile({ ...userProfile, sex: 'other' })}
                            />
                            <label>Other</label>
                          </div>
                        ) : (
                          <span>{userProfile.sex}</span>
                        )}
                        <button
                          onClick={() => setEditField(editField === 'sex' ? null : 'sex')}
                          className="w-8 h-8 bg-gray-200 rounded-full flex justify-center items-center"
                        >
                          <Pencil className="h-4 w-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </header>

        <main>
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 space-y-2 sm:space-y-0">
            <h2 className="text-xl sm:text-2xl font-semibold text-black">NFTs for Sale</h2>
            <Link href="/nft-marketplace" passHref>
              <button className="w-full sm:w-auto bg-yellow-400 hover:bg-yellow-500 text-black py-2 px-4 rounded-lg">
                View All NFTs
              </button>
            </Link>
          </div>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6">
            {currentNfts.map((nft) => (
              <div key={nft.id} className="bg-white shadow-lg rounded-lg overflow-hidden">
                <img src={nft.image} alt={nft.name} className="w-full h-48 object-cover" />
                <div className="p-4">
                  <h3 className="text-lg font-semibold text-black ">{nft.name}</h3>
                  <p className="text-base font-semibold text-yellow-600">{nft.price}</p>
                  <button className="w-full mt-2 bg-yellow-400 hover:bg-yellow-500 text-black text-sm py-2 px-4 rounded-lg">
                    Buy Now
                  </button>
                </div>
              </div>
            ))}
          </div>
          <div className="mt-6 flex justify-center items-center space-x-4">
            <button
              onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
              className="bg-yellow-400 hover:bg-yellow-500 text-black py-2 px-4 rounded-lg flex items-center"
            >
              <ChevronLeft className="h-4 w-4 mr-2" />
              Previous
            </button>
            <span className="text-sm font-medium text-black">
              Page {currentPage} of {totalPages}
            </span>
            <button
              onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
              disabled={currentPage === totalPages}
              className="bg-yellow-400 hover:bg-yellow-500 text-black py-2 px-4 rounded-lg flex items-center"
            >
              Next
              <ChevronRight className="h-4 w-4 ml-2" />
            </button>
          </div>
        </main>
      </div>
    </div>
  )
}
